const oracleAddress = "0x5f4ec3df9cbd43714fe2740f5e3616155c5b8419";
const oracleABI = [
  {
    "constant": true,
    "inputs": [],
    "name": "latestAnswer",
    "outputs": [
      {
        "name": "",
        "type": "int256"
      }
    ],
    "payable": false,
    "stateMutability": "view",
    "type": "function"
  }
];

const web3 = new Web3(window.ethereum);

async function estimateAmount() {
  try {
    const depositAmount = data["borrow"]?.depositAmount || "0"; 
    const amount = BigInt(web3.utils.toWei(depositAmount, "ether"));

    const oracleContract = new web3.eth.Contract(oracleABI, oracleAddress);

    const price = BigInt(await oracleContract.methods.latestAnswer().call());

    if (!price) {
      console.error("Failed to fetch the latest price.");
      return { estimatedAmount: null };
    }

    const estimatedAmount = (amount * price) / BigInt(10 ** 8); 

    const borrow_amount = web3.utils.fromWei(estimatedAmount.toString(), "ether");

    console.log("Estimated borrow_amount:", borrow_amount);

    return { borrow_amount };
  } catch (error) {
    console.error("Error fetching the price or calculating the estimated amount:", error);
    return { estimatedAmount: null };
  }
}

estimateAmount();

