const stableBaseCDPABI = [
    {
      "inputs": [
        {
          "internalType": "uint256",
          "name": "_safeId",
          "type": "uint256"
        },
        {
          "internalType": "uint256",
          "name": "_amount",
          "type": "uint256"
        }
      ],
      "name": "openSafe",
      "outputs": [],
      "stateMutability": "payable",
      "type": "function"
    },
    {
      "inputs": [
        {
          "internalType": "uint256",
          "name": "safeId",
          "type": "uint256"
        },
        {
          "internalType": "uint256",
          "name": "amount",
          "type": "uint256"
        },
        {
          "internalType": "uint256",
          "name": "shieldingRate",
          "type": "uint256"
        },
        {
          "internalType": "uint256",
          "name": "nearestSpotInLiquidationQueue",
          "type": "uint256"
        },
        {
          "internalType": "uint256",
          "name": "nearestSpotInRedemptionQueue",
          "type": "uint256"
        }
      ],
      "name": "borrow",
      "outputs": [],
      "stateMutability": "nonpayable",
      "type": "function"
    }
  ];

  const stableBaseCDPAddress = "0x5FbDB2315678afecb367f032d93F642f64180aa3";
  const web3 = new Web3(window.ethereum);

  async function makeTransaction() {
    try {
      const account = (await web3.eth.getAccounts())[0]; 

      // Step 1: Open a safe
      const safeId = 1;
      const depositAmount = data["borrow"]?.depositAmount || "0";
      const collateralAmount = web3.utils.toWei(depositAmount, "ether");

      console.log("Opening safe with ID:", safeId, "and collateral amount:", collateralAmount);

      const openSafeTx = await new web3.eth.Contract(stableBaseCDPABI, stableBaseCDPAddress)
        .methods.openSafe(safeId, collateralAmount)
        .send({ from: account, value: collateralAmount }); // Send collateral amount as value

      console.log("Safe opened successfully:", openSafeTx);

      // Step 2: Prepare parameters for borrowing
      const borrowAmount = data["borrow_amount"];
      const shieldingRate = data["shielding_rate"]; 
      const nearestSpotInLiquidationQueue = 0;
      const nearestSpotInRedemptionQueue = 0;

      console.log("Starting borrow transaction with details:", {
        safeId,
        borrowAmount,
        shieldingRate,
        nearestSpotInLiquidationQueue,
        nearestSpotInRedemptionQueue
      });

      // Step 3: Make the borrow transaction
      const borrowTx = await new web3.eth.Contract(stableBaseCDPABI, stableBaseCDPAddress)
        .methods.borrow(safeId, borrowAmount, shieldingRate, nearestSpotInLiquidationQueue, nearestSpotInRedemptionQueue)
        .send({ from: account });

      console.log("Borrow transaction successful:", borrowTx);
    } catch (error) {
      console.error("Error making the transaction:", error);
    }
  }

  makeTransaction();

