function descriptionText() {
    const shieldingRate = data["shielding_rate"];
    const amount = data["borrow_amount"];
    let description;

    if (shieldingRate < 0.5) {
        description = `Shielding rate is below 0.5, indicating a lower level of shielded assets. Borrow estimated amount: ${amount}.`;
    } else if (shieldingRate == 0.5) {
        description = `Shielding rate is exactly 0.5, representing a balanced shielded assets ratio. Borrow estimated amount: ${amount}.`;
    } else if (shieldingRate > 0.5) {
        description = `Shielding rate is above 0.5, indicating a higher level of shielded assets. Borrow estimated amount: ${amount}.`;
    }

    const borrowEstimatedAmount = { description };
    return borrowEstimatedAmount;
}

descriptionText();
